
public class Pro02_Ch22_Point<T> {
	private T x, y;
	
	public Pro02_Ch22_Point(T x, T y) {
		this.x = x;
		this.y = y;
	}
	public void set(T o1, T o2) {
		x = o1;
		y = o2;
	}
	public T getX() {
		return x;
	}
	public T getY() {
		return y;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Pro02_Ch22_Point<Integer> g1 = new Pro02_Ch22_Point<>(1, 2);
		g1.set(10, 20);
		System.out.println(g1.getX() + ", " + g1.getY());
		
		Pro02_Ch22_Point<Double> g2 = new Pro02_Ch22_Point<>(1.1, 1.2);
		g2.set(1.2, 3.4);
		System.out.println(g2.getX() + ", " + g2.getY());
	}
}
